package org.poutsjr.cra.model;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;/** * Created with IntelliJ IDEA. * User: poutsjr * Date: 06/03/13 * Time: 23:16 * To change this template use File | Settings | File Templates. */@Entity@Table(name = "customer")public class Customer {    @Id    @Column    Long id;    @Column(name = "customer_name")    String customerName;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getCustomerName() {        return customerName;    }    public void setCustomerName(String customerName) {        this.customerName = customerName;    }}