package org.poutsjr.cra.model;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.Id;import javax.persistence.Table;import java.util.Date;/** * Created with IntelliJ IDEA. * User: poutsjr * Date: 06/03/13 * Time: 23:13 * To change this template use File | Settings | File Templates. */@Entity@Tablepublic class Person {    @Id    @Column(name = "id")    Long id;    @Column(name = "lastname")    String lastname;    @Column(name = "firstname")    String firstname;    @Column(name = "birthdate")    Date birthdate;    @Column(name = "beginDate")    Date beginDate;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getLastname() {        return lastname;    }    public void setLastname(String lastname) {        this.lastname = lastname;    }    public String getFirstname() {        return firstname;    }    public void setFirstname(String firstname) {        this.firstname = firstname;    }    public Date getBirthdate() {        return birthdate;    }    public void setBirthdate(Date birthdate) {        this.birthdate = birthdate;    }    public Date getBeginDate() {        return beginDate;    }    public void setBeginDate(Date beginDate) {        this.beginDate = beginDate;    }}